      rawCode: "\"use client\";\n\nimport React from \"react\";\nimport {\n    Accordion,\n    AccordionItem,\n    AccordionTrigger,\n    AccordionContent\n} from \"../components/accordion\";\nimport { Plus } from \"lucide-react\";\n\nexport default function AccordionDemo() {\n    return (\n        <Accordion className=\"w-full md:w-[500px]\">\n            <AccordionItem id=\"item-1\">\n                <AccordionItem id=\"item-1\">\n                    <AccordionTrigger>\n                        What is React?\n                        <Plus className=\"size-4 shrink-0 transition-all ease-out group-data-[state=open]:rotate-45\" />\n                    </AccordionTrigger>\n                    <AccordionContent>\n                        React is a JavaScript library for building user interfaces. It lets you\n                        create reusable UI components.\n                    </AccordionContent>\n                </AccordionItem>\n            </AccordionItem>\n            <AccordionItem id=\"item-2\">\n                <AccordionTrigger>\n                    <span>What is Tailwind CSS?</span>\n                    <Plus className=\"size-4 shrink-0 transition-all ease-out group-data-[state=open]:rotate-45\" />\n                </AccordionTrigger>\n                <AccordionContent>\n                    Tailwind CSS is a utility-first CSS framework packed with classes like flex,\n                    pt-4, text-center and rotate-90 that can be composed to build any design,\n                    directly in your markup.\n                </AccordionContent>\n            </AccordionItem>\n\n            <AccordionItem id=\"item-3\">\n                <AccordionTrigger>\n                    Why use Framer Motion?\n                    <Plus className=\"size-4 shrink-0 transition-all ease-out group-data-[state=open]:rotate-45\" />\n                </AccordionTrigger>\n                <AccordionContent>\n                    Framer Motion is a motion library for React. It provides a declarative, flexible\n                    API to add smooth animations and gestures to your components.\n                </AccordionContent>\n            </AccordionItem>\n        </Accordion>\n    );\n}\n",
