      rawCode: "\"use client\";\n\nimport React from \"react\";\nimport { cn } from \"@/lib/utils\";\nimport { motion, Variants, Transition, AnimatePresence } from \"framer-motion\";\n\ntype AnimationType = \"line\" | \"none\";\n\ninterface TabContextType {\n    activeTab: string;\n    setActiveTab: (tab: string) => void;\n    animationType: AnimationType;\n    instanceId: string;\n}\n\nconst TabContext = React.createContext<TabContextType | null>(null);\n\nconst useTabContext = () => {\n    const context = React.useContext(TabContext);\n    if (!context) {\n        throw new Error(\"Tab components must be used within a Tabs component\");\n    }\n    return context;\n};\n\n/* -------------------------------------------------------------------------------------------------\n * Tabs component\n * -------------------------------------------------------------------------------------------------\n */\ninterface TabsProps {\n    children: React.ReactNode;\n    defaultTab: string;\n    className?: string;\n    animationType?: AnimationType;\n    instanceId?: string;\n}\n\nconst Tabs = ({\n    children,\n    defaultTab,\n    className,\n    animationType = \"line\",\n    instanceId = Math.random().toString(36).slice(2)\n}: TabsProps) => {\n    const [activeTab, setActiveTab] = React.useState(defaultTab);\n\n    return (\n        <TabContext.Provider value={{ activeTab, setActiveTab, animationType, instanceId }}>\n            <div className={cn(\"w-full\", className)}>{children}</div>\n        </TabContext.Provider>\n    );\n};\n\n/* -------------------------------------------------------------------------------------------------\n * TabList component\n * -------------------------------------------------------------------------------------------------\n */\ninterface TabListProps {\n    children: React.ReactNode;\n    className?: string;\n}\n\nconst TabList = ({ children, className }: TabListProps) => {\n    return (\n        <div\n            className={cn(\n                \"relative flex gap-2 border-b border-gray-200 dark:border-zinc-700\",\n                className\n            )}\n        >\n            {children}\n        </div>\n    );\n};\n\n/* -------------------------------------------------------------------------------------------------\n * Tab component\n * -------------------------------------------------------------------------------------------------\n */\ninterface TabProps {\n    children: React.ReactNode;\n    value: string;\n    className?: string;\n    icon?: React.ReactNode;\n}\n\nconst Tab = ({ children, value, className, icon }: TabProps) => {\n    const { activeTab, setActiveTab, animationType, instanceId } = useTabContext();\n    const isActive = activeTab === value;\n\n    const getAnimationProps = () => {\n        switch (animationType) {\n            case \"line\":\n                return {\n                    layoutId: `activeTab-${instanceId}`,\n                    className:\n                        \"absolute -bottom-[1px] left-0 right-0 h-[2px] rounded-full bg-black dark:bg-white\"\n                };\n            default:\n                return {};\n        }\n    };\n\n    return (\n        <motion.button\n            onClick={() => setActiveTab(value)}\n            className={cn(\n                \"relative px-4 py-2 text-sm font-medium transition-colors flex items-center gap-2\",\n                {\n                    \"text-gray-500 hover:text-black dark:text-gray-400 dark:hover:text-white\":\n                        !isActive\n                },\n                className\n            )}\n        >\n            {icon && <span>{icon}</span>}\n            {children}\n            {isActive && animationType !== \"none\" && <motion.div {...getAnimationProps()} />}\n        </motion.button>\n    );\n};\n\n/* -------------------------------------------------------------------------------------------------\n * TabPanels component\n * -------------------------------------------------------------------------------------------------\n */\n\ninterface TabPanelsProps {\n    children: React.ReactNode;\n    className?: string;\n}\n\nconst TabPanels = ({ children, className }: TabPanelsProps) => {\n    return (\n        <div className={cn(\"mt-3 p-3 rounded border dark:border-zinc-700\", className)}>\n            {children}\n        </div>\n    );\n};\n\n/* -------------------------------------------------------------------------------------------------\n * TabPanel component\n * -------------------------------------------------------------------------------------------------\n */\n\ninterface TabPanelProps {\n    children: React.ReactNode;\n    value: string;\n    className?: string;\n    variants?: Variants;\n    transition?: Transition;\n}\n\nconst TabPanel = ({\n    children,\n    value,\n    className,\n    variants = undefined,\n    transition = undefined\n}: TabPanelProps) => {\n    const { activeTab } = useTabContext();\n    if (activeTab !== value) return null;\n\n    return (\n        <AnimatePresence mode=\"wait\">\n            {activeTab === value && (\n                <motion.div\n                    initial=\"initial\"\n                    animate=\"animate\"\n                    exit=\"exit\"\n                    variants={variants}\n                    transition={transition}\n                    className={cn(className)}\n                >\n                    {children}\n                </motion.div>\n            )}\n        </AnimatePresence>\n    );\n};\n\nexport { Tabs, TabList, Tab, TabPanels, TabPanel };\n",
