      rawCode: "\"use client\";\n\nimport React from \"react\";\nimport {\n    Dialog,\n    DialogTrigger,\n    DialogContent,\n    DialogHeader,\n    DialogTitle,\n    DialogDescription,\n    DialogClose\n} from \"@/content/registry/core/dialog\";\nimport { Variants, Transition } from \"framer-motion\";\n\nconst customVariants: Variants = {\n    initial: { opacity: 0, y: \"100%\" },\n    animate: { opacity: 1, y: \"0%\" },\n    exit: { opacity: 0, transition: { duration: 0.3 } }\n};\n\nconst customTransition: Transition = {\n    type: \"spring\",\n    damping: 30,\n    stiffness: 300\n};\n\nexport default function DialogCustomVariantTransion() {\n    return (\n        <Dialog variants={customVariants} transition={customTransition}>\n            <DialogTrigger>\n                <button className=\"px-4 py-2 bg-gray-200 rounded\">Open Dialog</button>\n            </DialogTrigger>\n            <DialogContent>\n                <DialogHeader>\n                    <DialogTitle>Dialog Title</DialogTitle>\n                    <DialogDescription>This is a dialog description.</DialogDescription>\n                </DialogHeader>\n                <p>This is the main content of the dialog.</p>\n                <div className=\"mt-6 flex justify-end\">\n                    <DialogClose>\n                        <button className=\"px-4 py-2 bg-gray-200 text-gray-800 rounded hover:bg-gray-300 mr-2\">\n                            Cancel\n                        </button>\n                    </DialogClose>\n                    <button className=\"px-4 py-2 bg-zinc-800 text-white rounded\">Confirm</button>\n                </div>\n            </DialogContent>\n        </Dialog>\n    );\n}\n",
